const burger=document.querySelector(".burger"),header=document.querySelector(".header");burger.addEventListener("click",()=>{burger.classList.toggle("burger--cross"),header.classList.toggle("header--open")});
const click=document.querySelector(".equipment__click"),info=document.querySelectorAll(".equipment__info"),list=document.querySelectorAll(".equipment__list");click.addEventListener("click",()=>{info.forEach(e=>{e.classList.toggle("equipment__info--close")}),list.forEach(e=>{e.classList.toggle("equipment__list--open")})});